.hero::before {
  content: '';
  height: 100%;
  width: 100%;
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  background: linear-gradient(60deg, #11005c, #00ffd1);
  background: radial-gradient(ellipse at bottom right, var(--colours--cobalt) 10%, transparent 40%), radial-gradient(ellipse at top left, var(--colours--violet) -20%, transparent 40%), linear-gradient(60deg, #11005c, #00ffd1);
  z-index: 1;
  mix-blend-mode: multiply;
}
.hero-border-wrapper {
  opacity: 0;
  transform: scale(1.2);
  transform-origin: 50% 50%;
  animation: 1000ms heroEaseIn ease forwards 1;
}
.hero-header {
  opacity: 0;
  transform: scale(1);
  transform-origin: 50% 50%;
  animation: 500ms heroEaseInChildren 1000ms ease forwards 1;
}
.hero-head-secondary-text {
  opacity: 0;
  transform: scale(1);
  transform-origin: 50% 50%;
  animation: 500ms heroEaseInChildren 1200ms ease forwards 1;
}
.hero .faux-list {
  opacity: 0;
  transform: scale(1);
  transform-origin: 50% 50%;
  animation: 500ms heroEaseInChildren 1400ms ease forwards 1;
}
@keyframes heroEaseIn {
  100% {
    opacity: 1;
    transform: scale(1);
  }
}
@keyframes heroEaseInChildren {
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

